mapper.select:
select issue_id, title, stage_id, create_time FROM s_issue where issue_id <= ( select issue_id FROM s_issue where state = 'U'
and issue_type = 1
and create_time >= #{serviceManage.createTimeBegin}
and create_time <= #{serviceManage.createTimeEnd}
and update_time >= #{serviceManage.updateTimeBegin}
and update_time <= #{serviceManage.updateTimeEnd}
order by issue_id desc limit #{startIndex},1 ) and state = 'U'
and issue_type = 1
and create_time >= #{serviceManage.createTimeBegin}
and create_time <= #{serviceManage.createTimeEnd}
and update_time >= #{serviceManage.updateTimeBegin}
and update_time <= #{serviceManage.updateTimeEnd}
order by issue_id desc limit #{serviceManage.pageSize}

