WITH epic as (SELECT DISTINCT issue_id FROM s_issue WHERE state = 'U' AND issue_id IN
#{item}
), feature as ( SELECT DISTINCT issue_id FROM s_issue WHERE state = 'U' AND parent_id IN (select issue_id from epic) ), story as ( SELECT DISTINCT issue_id FROM s_issue WHERE state = 'U' AND parent_id IN (select issue_id from feature) ),
task as ( SELECT DISTINCT issue_id FROM s_issue WHERE state = 'U' AND parent_id IN (select issue_id from story) )
select DISTINCT issue_id from epic union all select DISTINCT issue_id from feature union all select DISTINCT issue_id from story union all select DISTINCT issue_id from task

