mapper.insert:
insert into s_project_user_day ( really_workload, work_date,
  work_uid, project_id, state, 
  create_uid, create_time, update_uid, 
  update_time, tenant_code, work_desc
  )
values ( #{reallyWorkload,jdbcType=INTEGER}, #{workDate,jdbcType=TIMESTAMP},
  #{workUid,jdbcType=BIGINT}, #{projectId,jdbcType=BIGINT}, #{state,jdbcType=VARCHAR}, 
  #{createUid,jdbcType=BIGINT}, #{createTime,jdbcType=TIMESTAMP}, #{updateUid,jdbcType=BIGINT}, 
  #{updateTime,jdbcType=TIMESTAMP}, #{tenantCode,jdbcType=VARCHAR}, #{workDesc,jdbcType=LONGVARCHAR}
  )

mapper.select:
SELECT COUNT(*) as totalDays, COUNT(*)*8 as totalPlanWorkload,SUM(really_workload) as totalReallyWorkload,work_uid as userId
FROM s_project_user_day
WHERE state='U' AND project_id = #{projectId}
AND work_uid = #{userId}
AND work_date >= #{startDate}
 AND work_date <= #{endDate}
GROUP BY work_uid
