mapper.update:

  update
    version_sync_data
  set
    sync_result = #{syncResult}
  where
    id <= #{id}
  and
    version_id = #{versionId}
  and
    operate_type = #{operateType}
  and
    project_id = #{projectId}
  and
    sync_system = #{syncSystem}
  and
    sync_result in (-1,0)
</update>
